import React, { useState } from 'react';
import axios from 'axios';

const OtpVerification = () => {
    const [email, setEmail] = useState('');
    const [otp, setOtp] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [error, setError] = useState('');  // State for error message
    const [loading, setLoading] = useState(false);  // To handle loading state

    const handleVerifyOtp = async (e) => {
        e.preventDefault();
        setLoading(true);  // Show loading indicator
        setError('');  // Clear previous errors
        try {
            await axios.post('http://localhost:5000/api/auth/verify-otp', { email, otp, newPassword });
            alert('Password reset successful');
            setLoading(false);  // Hide loading after successful password reset
        } catch (error) {
            setLoading(false);  // Hide loading after error
            if (error.response) {
                setError(error.response.data.message);  // Show error message from server
            } else {
                setError('Error connecting to the server. Please try again later.');
            }
        }
    };

    return (
        <div>
            <h2>Verify OTP and Reset Password</h2>
            <form onSubmit={handleVerifyOtp}>
                <div>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email"
                        required
                    />
                </div>
                <div>
                    <input
                        type="text"
                        value={otp}
                        onChange={(e) => setOtp(e.target.value)}
                        placeholder="OTP"
                        required
                    />
                </div>
                <div>
                    <input
                        type="password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        placeholder="New Password"
                        required
                    />
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display error message */}
                <button type="submit" disabled={loading}>
                    {loading ? 'Verifying OTP...' : 'Verify OTP'}
                </button>
            </form>
        </div>
    );
};

export default OtpVerification;
