import React, { useState } from 'react';
import axios from 'axios';
import { NavLink } from 'react-router-dom'; // Import NavLink

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');  // State to store error message
    const [loading, setLoading] = useState(false);  // To handle loading state

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);  // Show loading indicator
        setError('');  // Clear any previous errors
        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
            alert('Login successful: ' + response.data.token);
            localStorage.setItem('token', response.data.token);  // Store token in localStorage
            setLoading(false);  // Hide loading after successful login
        } catch (error) {
            setLoading(false);  // Hide loading after error
            if (error.response) {
                setError(error.response.data.message);  // Show error message from server
            } else {
                setError('Error connecting to the server. Please try again later.');
            }
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                <div>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email"
                        required
                    />
                </div>
                <div>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Password"
                        required
                    />
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display error message */}
                
                {/* Forgot Password Link */}
                <NavLink to="/forgot-password" style={{ display: 'block', margin: '10px 0', textAlign: 'center' }}>
                    Forgot Password?
                </NavLink>

                <button type="submit" disabled={loading}>
                    {loading ? 'Logging in...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
